import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { useCookies } from "react-cookie";
import MainLayout from "..//..//layout//MainLayout.jsx";
import { Modal, Button } from "react-bootstrap";

// bootstrap imports
import {
  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBInput,
  MDBValidation,
  MDBValidationItem,
  
} from "mdb-react-ui-kit";

// Login Function  login to the app
function Login(props) {
    const navigate = useNavigate();
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [, setCookies] = useCookies(["access_token"]);
    const [showModal, setShowModal] = useState(false);
    const [modalOption, setModalOption] = useState("");
    const [modalMessage, setModalMessage] = useState("");
  
    // Logic for handling form submission goes here
    const handleSubmit = async (event) => {
      event.preventDefault();
      try {
        const response = await axios.post("http://localhost:3002/auth/login", {
          email: email,
          password: password,
        });
  
        setModalOption("success");
        setShowModal(true);
        setModalMessage(response.data.message);
        window.localStorage.setItem("access_token", response.data.token);
        window.localStorage.setItem("userId", response.data.userID);
  
      } catch (err) {
        setModalOption("error");
        setShowModal(true);
        console.error(err);
      }
    };
    // handle email and password change
    const handleEmailChange = (event) => {
      setEmail(event.target.value);
    };
  
    const handlePasswordChange = (event) => {
      setPassword(event.target.value);
    };
  
    const handleModalClose = () => {
      setShowModal(false);
      setShowModal(false);
  
      if (modalOption === "success") {
        navigate("/userpage");
      }
    };
  
    return (
      <>
        <MainLayout>
          <MDBContainer fluid onSubmit={handleSubmit}  >
            <div className="p-5 bg-image" style={{ backgroundImage: 'url(https://mdbootstrap.com/img/new/textures/full/171.jpg)', height: '300px' }}></div>
            <MDBRow className='d-flex justify-content-center align-items-center h-100'>
              <MDBCol col='12'>
                <MDBCard className='bg-dark text-white mx-auto' style={{ borderRadius: '1rem', maxWidth: '400px',marginTop: '-100px', background: 'hsla(0, 0%, 100%, 0.8)', backdropFilter: 'blur(30px)' }} >
                  <MDBCardBody className='p-5 d-flex flex-column align-items-center mx-auto w-100'>
                    <h2 className="fw-bold mb-2 text-uppercase">Login</h2>
                    <p className="text-white-50 mb-5">Please enter your login and password!</p>
                    <MDBValidation className='row g-3' >
                      <MDBValidationItem feedback='Please choose a Email.' invalid className='col-md-12' >
                        <MDBInput
                          value={email}
                          name='email'
                          onChange={handleEmailChange}
                          id='email'
                          required
                          label='Email address'
                          type='email'
                          size="lg"
                          labelClass='text-white'
                          style={{ color: 'white' }}
                        />
                      </MDBValidationItem>
                      <MDBValidationItem feedback='Please choose a Password.' invalid className='col-md-12'>
                        <MDBInput
                          value={password}
                          name='password'
                          onChange={handlePasswordChange}
                          id='password'
                          required
                          label='Password'
                          type='password'
                          size="lg"
                          labelClass='text-white'
                          inputClass='text-white'
                          style={{ color: 'white' }}
                        />
                      </MDBValidationItem>
                      <div className="d-flex justify-content-center">
                        <MDBBtn
                          outline
                          className='mx-2 px-5'
                          color='white'
                          size='lg'
                          type='submit'
                          inputClass='text-white'
                          data-mdb-toggle="modal"
                          data-mdb-target="#exampleModal"
                          style={{ margin: '2vh' }}>
                          Login
                        </MDBBtn>
                      </div>
                    </MDBValidation>
  
                    <div>
                      <p className="mb-0">Don't have an account? <a href="register" class="text-white-50 fw-bold">Sign Up</a></p>
                    </div>
                  </MDBCardBody>
                </MDBCard>
              </MDBCol>
            </MDBRow>
          </MDBContainer>
  
          <Modal show={showModal} onHide={() => setShowModal(false)}>
            <Modal.Header closeButton={modalOption === "error"}>
              <Modal.Title>Login</Modal.Title>
            </Modal.Header>
  
            <Modal.Body>
              {/* Two Options to SHow */}
              {modalOption === "error" && (
                <p>
                  Error occurred while trying to login , User Name or Password
                  Incorrect {modalMessage} .
                </p>
              )}
              {modalOption === "success" && <p>Logged In Successfully </p>}
            </Modal.Body>
            <Modal.Footer>
              {modalOption === "success" && (
                <Button variant="primary" onClick={handleModalClose}>
                  {" "}
                  OK{" "}
                </Button>
              )}
              {modalOption !== "success" && (
                <Button variant="secondary" onClick={() => setShowModal(false)}>
                  Close
                </Button>
              )}
            </Modal.Footer>
          </Modal>
        </MainLayout>
      </>
    );
  }
  export default Login;
